import styled from 'styled-components';

export const DetailsButton = styled.button`
  background-color: #4290E8;
  color: white;
  font-size: 15px;
  padding: 5px 20px;
  border-radius: 5px;
  margin: 10px 20px;
  cursor: pointer;

  &:hover {
    background-color: #006de8;
    font-weight: bold;
    cursor: pointer
  }
`;

export const FormSubmitButton = styled.button`
  background-color: #4290E8;
  color: white;
  font-size: 15px;
  padding: 5px 20px;
  border-radius: 5px;
  margin: 10px 20px;
  cursor: pointer;

  &:hover {
    background-color: #006de8;
    font-weight: bold;
    cursor: pointer
  }
`;
