import React, { Component } from 'react';
import './Home.css';
import { GeneralButton } from '../components/Buttons';
import './Notes.css';
import axios from 'axios';

// No Results
export const NoResults = () => {
  return (
    <tr>
      <td></td>
      <td></td>
      <td></td>
      <td></td>
    </tr>
  );
};

// View & Add Notes
class Form extends Component{

  constructor(props){
      super(props)
      this.state = {
          codename: '',
          clientName: '',
          
      }
  }

  handleFormSubmit( event ) {
    event.preventDefault();
    axios({
        method: 'post',
        url: `http://localhost/soteria_cat_project/cat_backend/caseList/index.php`,
        headers: { 'content-type': 'application/json' },
        data: this.state
      })
        .then(result => {
          this.setState({
            mailSent: result.data.sent
          })
        })
  }

  render(){
    
    return(
    <main>
        <p>TEST POST</p>
        <div>
            <form action="#">
                
                <label>Codename</label>
                <input type="test" name='codename' value={this.state.codename} 
                    onChange={e => this.setState({ codename: e.target.value })}></input>
                
                <GeneralButton type='submit' onClick={e => this.handleFormSubmit(e)} 
                    name='action' value='Details'>POST</GeneralButton>
            </form>
        </div>
    </main>
    )
  }
};

export const TEST = () => {
    return (
      <Form />
    );
  };
